import React, { useEffect } from 'react';
import Editor from '../markdown/Editor';
import { connect } from "react-redux";
import PropTypes from "prop-types";

import MainFeature from './MainFeature';
import { Container } from '@material-ui/core';
import code1 from '../img/code1.jpg';
import code2 from '../img/code2.jpg';
import code3 from '../img/code3.jpg';
import Grid from '@material-ui/core/Grid';
import FeaturePost from './FeaturePost';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { getPosts } from '../actions/blog';
const useStyles = makeStyles((theme) => ({
    mainFeaturedPost: {
        position: 'relative',
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
        marginBottom: theme.spacing(6),
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }
}));
const Blog = ({ getPosts, blog }) => {
    const classes = useStyles();
    useEffect(() => {
        getPosts();
    }, [getPosts])

    return (
        <Container>
            <MainFeature post={blog.posts} image={code1} />
            <Paper className={classes.mainFeaturedPost} style={{ backgroundImage: `url(${code1})` }}></Paper>
            <Grid container spacing={4}>
                {blog.posts.slice(0, 2).map((post) => (
                    <FeaturePost key={post.title} post={post} />
                ))}
            </Grid>
        </Container>
    )
}

Blog.propTypes = {
    getPosts: PropTypes.func.isRequired
    //posts: PropTypes.array
}
const mapStateToProps = state => ({
    blog: state.blog
})

export default connect(mapStateToProps, { getPosts })(Blog);
