import React, { useState } from 'react'
import MEditor from '../markdown/MEditor';

const NewPost = () => {
    const [markdown, setMarkdown] = useState()
    return (
        <MEditor value={markdown} onChange={e => {
            setMarkdown(e.target.value)
        }} />
    )
}

export default NewPost
