import React from 'react';
import axios from 'axios';
import { GET_POST, POST_ERROR } from './types';


export const getPosts = () => async dispatch => {
    try {
        const res = await axios.get("/blogs");
        console.log(res.data);

        dispatch({
            type: GET_POST,
            payload: res.data.records
        });
    } catch (err) {
        dispatch({
            type: POST_ERROR
        })
    }
}
