import React, { useEffect } from 'react';
import Markdown from './Markdown';
import { Paper, Container } from '@material-ui/core';
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getPost } from '../actions/blog';

const BlogDetail = ({ match, getPost, blog: { post, loading } }) => {

    getPost(match.params.id);

    return (
        <React.Fragment>
            <Container>
                <Paper>
                    {/* {!loading && (<Markdown key={String(post.title)}>
                        {String(post.content)}
                    </Markdown>)} */}
                    {!loading && (<div>{post.content}</div>)}
                </Paper>
            </Container>
        </React.Fragment>
    )
}

BlogDetail.propTypes = {
    getPost: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    blog: state.blog
})
export default connect(mapStateToProps, { getPost })(BlogDetail);
