import React, { useEffect } from 'react';
import Editor from '../markdown/Editor';
import { connect } from "react-redux";
import PropTypes from "prop-types";

import MainFeature from './MainFeature';
import { Container } from '@material-ui/core';
import code1 from '../img/code1.jpg';
import code2 from '../img/code2.jpg';
import code3 from '../img/code3.jpg';
import Grid from '@material-ui/core/Grid';
import FeaturePost from './FeaturePost';
import { getPosts } from '../actions/blog';
const mainFeaturedPost = {
    title: 'Title of a longer featured blog post',
    description:
        "Multiple lines of text that form the lede, informing new readers quickly and efficiently about what's most interesting in this post's contents.",
    image: code1,
    imgText: 'Main',
    linkText: 'Continue reading…',
};
const featuredPosts = [
    {
        title: 'Featured post',
        date: 'Nov 12',
        description:
            'This is a wider card with supporting text below as a natural lead-in to additional content.',
        image: code2,
        imageText: 'Image Text',
    },
    {
        title: 'Post title',
        date: 'Nov 11',
        description:
            'This is a wider card with supporting text below as a natural lead-in to additional content.',
        image: code3,
        imageText: 'Image Text',
    },
];
const Blog = ({ getPosts, posts }) => {

    useEffect(() => {
        getPosts()
    }, [])
    return (
        <Container>
            <MainFeature post={mainFeaturedPost} />
            <Grid container spacing={4}>
                {posts.map((post) => (
                    <FeaturePost key={post.title} post={post} />
                ))}
            </Grid>
        </Container>
    )
}

// Blog.propTypes = {
//     getPosts: PropTypes.func.isRequired,
//     posts: PropTypes.array.isRequired
// }
const mapStateToProps = state => ({
    posts: state.blog.posts
})

export default connect(mapStateToProps, { getPosts })(Blog);
