import React from 'react';
import axios from 'axios';
import { LOGIN_SUCCESS } from './types';


export const login = ({ username, password }) => async dispatch => {

    try {
        const res = await axios.post("/login", {
            username: username,
            password: password
        })
        dispatch({
            type: LOGIN_SUCCESS,
            payload: res.data
        })
    } catch (err) {
        console.log(err);

    }
}
