import React, { useState } from 'react'
import MdEditor from 'react-markdown-editor-lite';
import MarkdownIt from 'markdown-it';
import hljs from 'highlight.js'
import 'highlight.js/styles/atom-one-light.css'
import 'react-markdown-editor-lite/lib/index.css';
import { Button, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Paper, { PaperProps } from '@material-ui/core/Paper';
import PropTypes from 'prop-types';

const useStyles = makeStyles((theme) => ({
    paper: {
        height: '800px',
        display: 'flex',
        overflow: 'auto',
        flexDirection: 'column',
    }
}));


const NewPost = ({ newPost }) => {

    const classes = useStyles();

    const [markdown, setMarkdown] = useState("")

    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    }
    const handleClose = () => {
        setOpen(false);
    }

    const mdParser = new MarkdownIt({
        html: true,
        linkify: true,
        typographer: true,
        highlight: function (str, lang) {
            if (lang && hljs.getLanguage(lang)) {
                try {
                    return hljs.highlight(lang, str).value
                } catch (__) {

                }
            }
            return '';
        }
    });
    const handleEditorChange = ({ html, text }) => {
        setMarkdown(text)
    }
    return (
        <div>
            <TextField
                id="standard-full-width"
                label="Title"
                placeholder="Please enter the article title"
                helperText="No more than 20 character!"
                fullWidth
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
            />
            <Paper className={classes.paper}>
                <MdEditor style={{ height: '100%' }}
                    renderHTML={(text) => mdParser.render(text)}
                    value={markdown} onChange={handleEditorChange
                    } />
            </Paper>
            <Button variant='contained' style={{ float: 'right', marginTop: '3%' }} onClick={handleClickOpen}>Post</Button>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="draggable-dialog-title"
            >
                <DialogTitle style={{ cursor: 'move' }} id="draggable-dialog-title">
                    Post New Article
                </DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        To post your article, please click the confirm button.
                </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button autoFocus onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleClose} color="primary">
                        Confirm
                     </Button>
                </DialogActions>
            </Dialog>
        </div>
    )
}

export default NewPost
