import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { getPosts } from '../actions/blog';
import PropTypes from 'prop-types';
import { connect } from "react-redux";
import { Paper, Table, TableHead, TableRow, TableCell, TableBody, TablePagination, Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  paper: {
    height: '800px',
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  }
}));


const Dashboard = ({ getPosts, blog }) => {

  const classes = useStyles();

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeRowsPerPage = event => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };
  const emptyRows =
    rowsPerPage - Math.min(rowsPerPage, blog.posts.length - page * rowsPerPage);


  useEffect(() => {
    getPosts()
  }, [])
  return (
    <Paper className={classes.paper}>

      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Id</TableCell>
            <TableCell>Title</TableCell>
            <TableCell>Date</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {blog.posts.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
            .map((post, index) => (
              <TableRow key={JSON.stringify(post.id)}>
                <TableCell>{post.id}</TableCell>
                <TableCell>{post.title}</TableCell>
                <TableCell>{post.created}</TableCell>
                <TableCell>{post.status}</TableCell>
                <TableCell><Button>Update</Button> <Button>Remove</Button></TableCell>
              </TableRow>
            ))
          }
        </TableBody>
      </Table>

      <TablePagination
        rowsPerPageOptions={[10, 15]}
        component="div"
        count={blog.posts.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}

Dashboard.propTypes = {
  getPosts: PropTypes.func.isRequired
}
const mapStateToProps = state => ({
  blog: state.blog
})
export default connect(mapStateToProps, { getPosts })(Dashboard);
