import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { Redirect } from "react-router-dom";
import { Grid, Typography, CardActionArea, Card } from '@material-ui/core';
import Link from '@material-ui/core/Link';
import Markdown from './Markdown';
import { GET_POST } from '../actions/types';
import { getPost } from '../actions/blog';
import { connect } from 'react-redux';

const useStyles = makeStyles((theme) => ({
    mainFeaturedPost: {
        position: 'relative',
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
        marginBottom: theme.spacing(4),
        backgroundImage: 'url(https://source.unsplash.com/random)',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    },
    overlay: {
        position: 'absolute',
        top: 0,
        bottom: 0,
        right: 0,
        left: 0,
        backgroundColor: 'rgba(0,0,0,.3)',
    },
    mainFeaturedPostContent: {
        position: 'relative',
        padding: theme.spacing(3),
        [theme.breakpoints.up('md')]: {
            padding: theme.spacing(6),
            paddingRight: 0,
        },
    },
}));


const MainFeaturedPost = ({ post, image }) => {
    const classes = useStyles();
    return (
        <Paper className={classes.mainFeaturedPost} style={{ backgroundImage: `url(${image})` }}>
            {/* Increase the priority of the hero background image */}
            {<img style={{ display: 'none' }} src={image} alt="jie's blog" />}
            <div className={classes.overlay} />
            <Grid container>
                <Grid item md={6}>
                    <div className={classes.mainFeaturedPostContent}>
                        <div>
                            <Markdown className={classes.markdown} key={post.id} >
                                {post.content.substring(0, 300)}
                            </Markdown>
                        </div>
                    </div>
                </Grid>
            </Grid>
        </Paper>
    );
}



export default MainFeaturedPost;
