import React from 'react';
import axios from 'axios';
import { GET_POST, POST_ERROR } from './types';


export const getPosts = () => async dispatch => {
    try {
        const res = await axios.get("/api/blogs");

        dispatch({
            type: GET_POST,
            payload: res.data.data
        });
    } catch (err) {
        dispatch({
            type: POST_ERROR
        })
    }
}
