import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { getPosts, removePost } from '../actions/blog';
import PropTypes from 'prop-types';
import UpdateIcon from '@material-ui/icons/Update';
import RemoveIcon from '@material-ui/icons/Remove';
import { connect } from "react-redux";
import { Paper, Table, TableHead, TableRow, TableCell, TableBody, TablePagination, Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  paper: {
    height: '800px',
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  }
}));


const Dashboard = ({ getPosts, removePost, blog }) => {

  const classes = useStyles();

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(8);
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeRowsPerPage = event => {
    setRowsPerPage(parseInt(event.target.value, 8));
    setPage(0);
  };
  const emptyRows =
    rowsPerPage - Math.min(rowsPerPage, blog.posts.length - page * rowsPerPage);

  const onClick = (id) => {
    removePost(id);
  }

  useEffect(() => {
    getPosts()
  }, [])
  return (
    <Paper className={classes.paper}>

      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Id</TableCell>
            <TableCell>Title</TableCell>
            <TableCell style={{ maxWidth: '250px' }}>Description</TableCell>
            <TableCell align="right">Date</TableCell>
            <TableCell align="right">Status</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {blog.posts.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
            .map((post, index) => (
              <TableRow key={JSON.stringify(post.id)}>
                <TableCell>{post.id}</TableCell>
                <TableCell>{post.title}</TableCell>
                <TableCell style={{ maxWidth: '250px' }}>{post.description}</TableCell>
                <TableCell align="right">{post.created}</TableCell>
                <TableCell align="right">{post.status}</TableCell>
                <TableCell align="right"><UpdateIcon /><Button color="primary">Update</Button> </TableCell>
                <TableCell align="right"><RemoveIcon /><Button color="secondary" onClick={() => onClick(post.id)}>Remove</Button></TableCell>
              </TableRow>
            ))
          }
        </TableBody>
      </Table>

      <TablePagination
        rowsPerPageOptions={[5, 8]}
        component="div"
        count={blog.posts.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}

Dashboard.propTypes = {
  getPosts: PropTypes.func.isRequired,
  removePost: PropTypes.func.isRequired
}
const mapStateToProps = state => ({
  blog: state.blog
})
export default connect(mapStateToProps, { getPosts, removePost })(Dashboard);
